import React, {Component} from 'react';
import styles from '../Styles/Categories.module.css';

class Display extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      data:[],
      id: "id-1",
      clicked:false
    }
  }

  componentDidMount (){
       
  

    
            fetch("https://api.thecatapi.com/v1/categories")
            .then(response => {
              // console.log(response);
              if(!response.ok) {
                throw Error("error")
              }
              return response.json()
              
              .then(allData => {allData.forEach(element => console.log( element ) ) 

                this.setState({
                  data:allData,
                  // id: event.target.id,
                  clicked:!this.state.clicked
                
                })
              })
              .catch (err => {
                throw Error (err.message);
              })
            })
          }
  
   


  render() {
    let { data } = this.state;
    return (
      <>
              <div className={styles.categoriesBlock}>
              {data.map(function(item, i){
        
                  return <div key={i}>{item.name}</div> 
               })}

                <div className={styles.category1}> </div>
                <div className={styles.category2}></div>
                <div className={styles.category3}></div>
                <div className={styles.category4}></div>
                <div className={styles.category5}></div>
                
              </div>
               
               
             
      </>
    );
  }
}

export default Display
