import React, {Component} from 'react';
import styles from '../Styles/Categories.module.css';

const TextBox = ({ label }) => (
  <React.Fragment>
    <label>{label}</label>
  </React.Fragment>
);

const Form = ({ data: { formData, gridLayout } }) => {
  return gridLayout.rows.map(row => {
    return (
      <div class="row">
        {row.column.map(col => {
          const textBoxProps = formData.find(data => data.objectId === col.id);
          // REPLACE col- with col-md-. Done here to demonstrate layout in small snippet output area
          return (
            <div className={`col-${col.width} borderedCol`}>
              <TextBox {...textBoxProps} />
            </div>
          );
        })}
      </div>
    );
  });
};

// ReactDOM.render(<Form data={data} />, document.getElementById("app"));
class Display extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      data:{},
      id: "id-1",
      clicked:false
    }
  }

  componentDidMount (){
         this.handleClick ();    
      }
  

      handleClick =event => {
            fetch("https://api.thecatapi.com/v1/categories")
            .then(response => {
              // console.log(response);
              if(!response.ok) {
                throw Error("error")
              }
              return response.json()
              
              .then(allData => {allData.forEach(element => element.name) 

                this.setState({
                  data:allData,
                  id: event.target[event.target.selectedIndex].id,
                  clicked:!this.state.clicked
                
                })
              })
              .catch (err => {
                throw Error (err.message);
              })
            })
      }
   


  render() {
    return (
      <>
      <div className={styles.categoriesBlock1}>
        <div className={styles.category1}> <button onClick={this.handleClick} className={styles.clicableButton}></button></div>
        <div className={styles.category2}></div>
        <div className={styles.category3}></div>
        <div className={styles.category4}></div>
        <div className={styles.category5}></div>
        
      </div>
      <div className={styles.categoriesBlock2}>
        <div className={styles.category6}></div>
        <div className={styles.category7}></div>
        <div className={styles.category8}></div>
        <div className={styles.category9}></div>
        <div className={styles.category10}></div>
      </div>
      </>
    );
  }
}

export default Display
