import React, {Component} from 'react';
import { Link } from 'react-router-dom';

class Item extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      data:[],
    }
  }

  componentDidMount (){
       
     fetch("https://api.thecatapi.com/v1/images/search?limit=10&page=1&category_ids=" + this.props.id)
            .then(response => {
              // console.log(response);
              if(!response.ok) {
                throw Error("error")
              }
              return response.json()
              
              .then(allData => {allData.forEach(element => console.log( element ) ) 

                this.setState({
                    data:allData,
                 })
              })
              .catch (err => {
                throw Error (err.message);
              })
      })
  }

  render() {
    let { data } = this.state;
    return (
      <> 
                <div> 
                    {data.map(function(item, i){ 
                        return <img width="100px" src={item.url} height="100px" key={i} /> 
                      })}
                </div>
               
      </>
    );
  }
}

export default Item;
