import React, {Component} from 'react';
import styles from '../Styles/Categories.module.css';

class Display extends React.Component {
  constructor(props){
    super(props);
  
    this.state = {
      data:[],
      clicked:false
    }
  }

  componentDidMount (){
       
     fetch("https://api.thecatapi.com/v1/categories")
            .then(response => {
              // console.log(response);
              if(!response.ok) {
                throw Error("error")
              }
              return response.json()
              
              .then(allData => {allData.forEach(element => console.log( element ) ) 

                this.setState({
                  data:allData,
                  clicked:!this.state.clicked
                
                })
              })
              .catch (err => {
                throw Error (err.message);
              })
            })
          }
  
   
          handleClick = () => {
            console.log('sas')
          }

  render() {
    let { data } = this.state;
    
    return (
      <>
              <div className={styles.categoriesBlock}> 
                    {data.map(function(item, i){ 
              
                        return <button key={i} onClick={this.handleClick}> 
                                    <h1 > {item.name}  </h1> 
                                    <p>Category</p>
                                </button> 
                                
                    })}
            
                

                </div>
               
      </>
    );
  }
}

export default Display
